import React, { useState } from 'react'
import './App.scss';
import Calc from "./components/Calc";
import Screen from './components/Screen';
import Board from './components/Board';
import Button from './components/Button';

const btnValues = [
  ["C", "AC", "%", "/"],
  [7, 8, 9, "X"],
  [4, 5, 6, "-"],
  [1, 2, 3, "+"],
  [0, ".", "="],
];

const numClickHandler = (e) => {
  e.preventDefault();
  const value = e.target.innerHTML;

  if(calc.num.length < 16) {
    setCalc({
      ...calc,
      num:
        calc.num === 0 && value === '0'
        ? '0'
        : calc.num % 1 === 0
        ? Number(calc.num + value)
        : calc.num + value,
      res: !calc.sign ? 0 : calc.res,
    });
  }
};

const App = () => {

  let [calc, setCalc] = useState({
    sign: '',
    num: 0,
    res: 0,
  });

  return (
   <>
    <Calc>
      <Screen value={calc.num ? calc.num : calc.res} />
      <Board>
        {
          btnValues.flat().map((btn, i) => {
            return (
              <Button 
                key={i}
                className={btn === "0" ? "equals" : ""}
                value={btn}
                onClick={
                  btn === "C"
                    ? resetClickHandler
                    : btn === "+-"
                    ? invertClickHandler
                    : btn === "%"
                    ? percentClickHandler
                    : btn === "="
                    ? equalsClickHandler
                    : btn === "/" || btn === "X" || btn === "-" || btn === "+"
                    ? signClickHandler
                    : btn === "."
                    ? commaClickHandler
                    : numClickHandler
                }/>
            )
          })
        }
      </Board>
    </Calc>
   </>
  );
}

export default App;
